mapper.select:
SELECT user_id friend_id,user_name, user_phone, user_img,user_area
FROM user
WHERE user_phone = #{userPhone}

SELECT COUNT(1)
FROM friend_request
WHERE 1 = 1

    AND user_id = #{userId}


    AND friend_id = #{friendId}


    request_state = #{requestState}


    AND is_read = #{isRead}

SELECT fq.request_id,fq.request_info,fq.create_date,fq.request_state,
u.user_id friend_id,u.user_name,u.user_img,u.user_sex
FROM friend_request fq
INNER JOIN user u
ON u.user_id = fq.user_id
WHERE 1 = 1

    AND friend_id = #{userId}


    AND request_id = #{requestId}


    AND is_received = #{isReceived}

ORDER BY fq.create_date DESC

mapper.update:
UPDATE friend_request


        is_read = #{isRead},


        is_received = #{isReceived},


        request_info = #{requestInfo},


        request_state = #{requestState},


WHERE


        user_id = #{userId}


        friend_id = #{friendId}


        request_id = #{requestId}

UPDATE friend


        friend_remark_name = #{friendRemarkName},


        friend_remark_text = #{friendRemarkText}


WHERE friend_id = #{friendId}

mapper.select:
SELECT COUNT(1)
FROM friend
WHERE user_id = #{userId}
AND friend_user_id = #{friendId}

SELECT COUNT(1)
FROM friend_request fr
WHERE 1 = 1

    AND fr.is_received = #{isReceived}


    AND fr.friend_id = #{friendId}

mapper.delete:
DELETE FROM friend
WHERE 1 = 1

    AND user_id = #{userId}


    AND friend_user_id = #{friendId}
