mapper.select:
SELECT goods_type_id, goods_type_name
FROM goods_typeinfo
WHERE goods_type_pid = #{typeId}

SELECT gd.goods_id,gd.goods_name,gd.goods_sales,imgsp.image_path, (SELECT gs.standard_price FROM goods_standard gs WHERE gs.goods_id = gd.goods_id ORDER BY standard_price LIMIT 0,1) goods_price FROM goodsinfo gd INNER JOIN goods_image img ON img.goods_id = gd.goods_id INNER JOIN image_space imgsp ON imgsp.image_id = img.image_id WHERE img.goods_img_type = 1 AND gd.my_team_id = #{type.myTeamId}
AND gd.goods_type_id IN
#{typeId}
AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
LIMIT #{pageNum},#{pageSize}

SELECT COUNT(0)
FROM goodsinfo gd
WHERE 1 = 1
AND gd.my_team_id = #{type.myTeamId}

    AND gd.goods_type_id = #{type.goodsTypeId}


    AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')

SELECT gs.standard_price,gs.standard_count
FROM goods_standard gs
WHERE standard_id = #{standardId}

SELECT gd.goods_describe
FROM goodsinfo gd
WHERE gd.goods_id = #{goodsId}

SELECT image_path
FROM image_space
WHERE image_id = #{id}

SELECT gt.goods_type_id, gt.goods_type_name,gt.goods_type_pid
FROM goods_typeinfo gt
WHERE gt.goods_type_pid = #{typeId}

SELECT gt.goods_type_id
FROM goods_typeinfo gt
WHERE gt.goods_type_pid = #{typeId}
