mapper.select:
SELECT goods_type_id, goods_type_name
FROM goods_typeinfo
WHERE goods_type_pid = #{typeId}

SELECT gd.goods_id,gd.goods_name,gd.goods_sales,imgsp.image_path, (SELECT MIN(gs.standard_price) FROM goods_standard gs WHERE gs.goods_id = gd.goods_id) goods_price FROM goodsinfo gd INNER JOIN goods_image img ON img.goods_id = gd.goods_id INNER JOIN image_space imgsp ON imgsp.image_id = img.image_id WHERE img.goods_img_type = 1 AND gd.my_team_id = #{type.myTeamId}
AND gd.goods_type_id IN
#{typeId}
AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
ORDER BY goods_price desc
ORDER BY goods_price asc
ORDER BY gd.goods_sales desc
ORDER BY gd.goods_sales asc
LIMIT #{pageNum},#{pageSize}

SELECT COUNT(0) FROM goodsinfo gd WHERE 1 = 1 AND gd.my_team_id = #{type.myTeamId}
AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
AND gd.goods_type_id IN
#{typeId}

SELECT gs.standard_price,gs.standard_count
FROM goods_standard gs
WHERE standard_id = #{standardId}

SELECT gd.goods_describe
FROM goodsinfo gd
WHERE gd.goods_id = #{goodsId}

SELECT image_path
FROM image_space
WHERE image_id = #{id}

SELECT gt.goods_type_id, gt.goods_type_name,gt.goods_type_pid
FROM goods_typeinfo gt
WHERE gt.goods_type_pid = #{typeId}

SELECT gt.goods_type_id
FROM goods_typeinfo gt
WHERE gt.goods_type_pid = #{typeId}

SELECT COUNT(0) FROM goodsinfo gd INNER JOIN qm_branch_goods qbg ON qbg.goods_id = gd.goods_id INNER JOIN qm_branch qb ON qb.branch_id = qbg.branch_id INNER JOIN user u ON u.team_department_id = qb.department_id INNER JOIN customer c ON c.promoter_id = u.user_id WHERE gd.my_team_id = #{type.myTeamId} AND c.user_id = #{type.userId}
AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
AND gd.goods_type_id IN
#{typeId}

SELECT gd.goods_id,gd.goods_name,gd.goods_sales, (SELECT MIN(gs.standard_price) FROM goods_standard gs WHERE gs.goods_id = gd.goods_id) goods_price FROM goodsinfo gd INNER JOIN qm_branch_goods qbg ON qbg.goods_id = gd.goods_id INNER JOIN qm_branch qb ON qb.branch_id = qbg.branch_id INNER JOIN user u ON u.team_department_id = qb.department_id INNER JOIN customer c ON c.promoter_id = u.user_id WHERE gd.my_team_id = #{type.myTeamId} AND c.user_id = #{type.userId}
AND gd.goods_type_id IN
#{typeId}
AND gd.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
ORDER BY goods_price desc
ORDER BY goods_price asc
ORDER BY gd.goods_sales desc
ORDER BY gd.goods_sales asc
LIMIT #{pageNum},#{pageSize}

SELECT branch_id
FROM qm_branch
WHERE my_team_id = #{myTeamId}
AND branch_type = #{branchType}
LIMIT 1

SELECT COUNT(0) FROM (SELECT gi.goods_id,gi.goods_name,gi.goods_sales, MIN(gs.standard_price) FROM goodsinfo gi INNER JOIN qm_branch_goods qbg ON qbg.goods_id = gi.goods_id INNER JOIN goods_standard gs ON gs.standard_id = qbg.standard_id WHERE qbg.branch_id = #{branchId}
AND gi.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
AND gi.goods_type_id IN
#{typeId}
GROUP BY gi.goods_id) t1

SELECT gi.goods_id,gi.goods_name,gi.goods_sales, MIN(gs.standard_price) goods_price FROM goodsinfo gi INNER JOIN qm_branch_goods qbg ON qbg.goods_id = gi.goods_id INNER JOIN goods_standard gs ON gs.standard_id = qbg.standard_id WHERE qbg.branch_id = #{branchId}
AND gi.goods_name LIKE CONCAT('%', #{type.keyword}, '%')
AND gi.goods_type_id IN
#{typeId}
GROUP BY gi.goods_id
ORDER BY gs.goods_price DESC
ORDER BY gs.standard_price ASC
ORDER BY gi.goods_sales desc
ORDER BY gi.goods_sales asc
LIMIT #{pageVO.pageNum},#{pageVO.pageSize}
