mapper.select:
SELECT td.team_department_id,td.team_department_name,td.create_date,
mt.my_team_name,
(SELECT COUNT(1) FROM user u WHERE u.team_department_id = td.team_department_id) count
FROM team_department td
INNER JOIN my_team mt
ON mt.my_team_id = td.my_team_id
WHERE td.my_team_id = #{myTeamId}

    AND td.department_type = #{departmentType}


    AND td.team_department_id = #{teamDepartmentId}

SELECT u.user_id,u.user_name,u.user_sex, u.user_phone,u.user_identity,u.user_img, mt.my_team_name, td.team_department_name, dp.position_name FROM user u INNER JOIN my_team mt ON mt.my_team_id = u.my_team_id INNER JOIN team_department td ON td.team_department_id = u.team_department_id INNER JOIN department_position dp ON dp.position_id = u.position_id WHERE 1 = 1
AND u.team_department_id = #{teamDepartmentId}
AND u.user_id = #{memberId}
ORDER BY dp.position_name DESC

SELECT u.user_id,u.user_name,u.user_sex, u.user_phone, td.team_department_name, dp.position_name FROM user u INNER JOIN team_department td ON td.team_department_id = u.team_department_id INNER JOIN department_position dp ON dp.position_id = u.position_id WHERE u.my_team_id = #{myTeamId}
AND u.team_department_id = #{teamDepartmentId}
AND (u.user_name LIKE CONCAT(#{userName},'%') OR u.user_phone = #{userName}) ORDER BY dp.position_name DESC

SELECT td.team_department_id,td.team_department_name,(SELECT COUNT(0) FROM user u WHERE u.team_department_id = td.team_department_id) count
FROM team_department td
WHERE my_team_id = #{myTeamId}
